package domain

// import statements are not shown.

@DomainClass @Entity @Canonical
class Invoice {

  @NotEmpty @Size(min=5, max=5)
  String number


  @NotNull @Type(type="org.jadira.usertype.dateandtime.joda.PersistentLocalDate")
  LocalDate date

  @ElementCollection @OrderColumn @NotEmpty
  List<LineItem> items = []

  void add(LineItem item) {
    items << item
  }

  BigDecimal total() {
    items.sum { it.total() }
  }

}

package domain

// import statements are not shown.

@Embeddable @Canonical
class LineItem {

  @NotNull @ManyToOne
  Product product

  @NotNull @Min(0l)
  BigDecimal price

  @NotNull @Min(1l)
  BigDecimal qty

  BigDecimal total() {
    price * qty
  }

}

